// Lee texto del agente (flexible por si cambia el campo)
const textoOriginal =
  ($json.output ?? $json.response ?? $json.text ?? "").toString();

// 0) Detectar token de silencio (<NO_REPLY>) y variantes
//    - Si el contenido es SOLO el token, suprimir.
//    - Si el token aparece mezclado con texto, eliminarlo.
const soloNoReply = /^\s*<?\s*NO[_\s-]*REPLY\s*>?\s*$/i.test(textoOriginal);

// Tambi√©n capturamos l√≠neas sueltas con el token para borrarlas
const reLineaNoReply = /^\s*<?\s*NO[_\s-]*REPLY\s*>?\s*$/gim;

// Si quer√©s adem√°s suprimir cuando se llamaron tools silenciosas, activ√° este bloque:
const tools = ($json.tools_used || $json.tools || []).map(String);
const silentTools = ["llamaToolRecepcionista", "llamaToolStudios"];
const handoffSilencioso = tools.some(t => silentTools.includes(t));

// 1) Pre-limpieza: eliminar cualquier aparici√≥n del token
let textoFiltradoBase = soloNoReply
  ? "" // si era SOLO el token, lo vaciamos
  : textoOriginal.replace(reLineaNoReply, "").replace(/<\s*NO[_\s-]*REPLY\s*>/gi, "");

// 2) Limpieza b√°sica (tu l√≥gica original)
let textoFiltrado = textoFiltradoBase
  .replace(/\n{3,}/g, "\n\n")   // Evita m√°s de 2 saltos seguidos
  .replace(/\r/g, "")           // Elimina retornos de carro
  .trim();

// 3) Eliminar markdown problem√°tico para WhatsApp
const limpio = textoFiltrado
  // Negritas markdown (**texto**) o (*texto*)
  .replace(/\*\*([^*]+)\*\*/g, "$1")
  .replace(/\*([^*]+)\*/g, "$1")
  // Bullets
  .replace(/^[-*+]\s+/gm, "‚Ä¢ ")
  // Enlaces [texto](url) -> url
  .replace(/\[([^\]]+)\]\((https?:\/\/[^\s)]+)\)/g, "$2")
  // Literales "\n"
  .replace(/\\n\\n/g, "\n\n")
  .replace(/\\n/g, "\n")
  // Espacios redundantes
  .replace(/[ \t]{2,}/g, " ")
  // Espacios al final de l√≠nea
  .replace(/[ \t]+$/gm, "")
  .trim();

// 4) Fallback: si qued√≥ vac√≠o, usar el texto original sin procesar (salvo que sea el token)
const mensajeFinal = limpio.length > 0 ? limpio : (soloNoReply ? "" : textoOriginal.trim());

// 5) Flag de supresi√≥n: si qued√≥ vac√≠o o hubo handoff silencioso
const suprimir = (mensajeFinal === "") || handoffSilencioso;

// Pod√©s usar `__suppressReply` en un IF para no enviar WhatsApp si es true.
// `texto_limpio` queda vac√≠o cuando era <NO_REPLY>.
return [{
  json: {
    texto_limpio: mensajeFinal,
    __suppressReply: suprimir,
    __reason: soloNoReply ? "no_reply_token" : (handoffSilencioso ? "silent_tool" : "ok")
  }
}];

