function parseTokenMap(s) {
  try {
    if (!s || typeof s !== 'string') return { parts: [], map: {} };

    const parts = s.split("|").map(p => String(p).trim()).filter(Boolean);
    const map = {};

    for (const part of parts) {
      const colonIndex = part.indexOf(":");
      if (colonIndex > 0) {
        const key = part.slice(0, colonIndex).trim().toLowerCase();
        const value = part.slice(colonIndex + 1).trim();
        if (key && value) {
          map[key] = value;
        }
      }
    }

    return { parts, map };
  } catch (error) {
    console.error('‚ùå Error parsing token map:', error);
    return { parts: [], map: {} };
  }
}

function normalizeToNull(value) {
  if (value === null || value === undefined) return null;
  const str = String(value).trim();
  if (!str || str.toUpperCase() === "N/D" || str === "-") return null;
  return str;
}

function normalizeDate(dateStr) {
  if (!dateStr) return null;
  try {
    if (/^\d{8}$/.test(dateStr)) return dateStr;
    const date = new Date(dateStr);
    if (isNaN(date.getTime())) return null;
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}${month}${day}`;
  } catch {
    return null;
  }
}

function normalizeTime(timeStr) {
  if (!timeStr) return null;
  try {
    if (/^\d{4}$/.test(timeStr)) return timeStr;
    const cleaned = timeStr.replace(/[^\d:]/g, '');
    const parts = cleaned.split(':');
    if (parts.length >= 2) {
      const hours = parseInt(parts[0]);
      const minutes = parseInt(parts[1]);
      if (hours >= 0 && hours <= 23 && minutes >= 0 && minutes <= 59) {
        return String(hours).padStart(2, '0') + String(minutes).padStart(2, '0');
      }
    }
    return null;
  } catch {
    return null;
  }
}

