// Router para gesti√≥n de turnos
const categoria = $('msg_clasificador').first().json.categoria;
const contactNumber = $('Variables').first().json.contactNumber;

// Mapeo de acciones
const accion = {
  categoria: categoria,
  contactNumber: contactNumber,
  // Determinar qu√© hacer
  accion: null
};

switch(categoria) {
  case 'NUEVO_PACIENTE':
    accion.accion = 'MOSTRAR_FORM_NUEVO_PACIENTE';
    break;
  case 'PACIENTE_REGISTRADO':
    accion.accion = 'SOLICITAR_DNI';
    break;
  case 'INICIAR_TOMAR_TURNO':
    accion.accion = 'MENU_TOMAR_TURNO';
    break;
  case 'INICIAR_CANCELAR_TURNO':
    accion.accion = 'LISTAR_TURNOS_CANCELAR';
    break;
  case 'LISTAR_MIS_TURNOS':
    accion.accion = 'MOSTRAR_MIS_TURNOS';
    break;
  default:
    accion.accion = 'DESCONOCIDO';
}

return [{ json: accion }];

