    timestamp: new Date().toISOString()
  }
}];

// ============================================
// CLASIFICADOR DE MENSAJES - SISTEMA JER√ÅRQUICO
// ============================================
// Autor: Sistema de Gesti√≥n de Turnos ICLP
// Versi√≥n: 2.0
// ============================================

// ============================================
// 1. CONFIGURACI√ìN Y CONSTANTES
// ============================================

const PATRONES = {
  MENU: {
    TURNOS: 'üìÖ TURNOS',
    INFO: '‚ÑπÔ∏è INFO GENERAL',
    ADMIN: 'üè¢ ADMINISTRACION',
    VOLVER: 'MENU PRINCIPAL'
  },

  TURNOS: {
    NUEVO: 'NUEVO PACIENTE',
    REGISTRADO: 'PACIENTE REGISTRADO',
    TOMAR: 'TOMAR TURNO',
    CANCELAR: 'CANCELAR TURNO',
    MIS_TURNOS: 'MIS TURNO/S'
  },

  TIPOS: {
    CONSULTORIO: 'TURNOS CONSULTORIO',
    ESTUDIO: 'TURNOS ESTUDIO'
  },

  INFO: {
    HORARIOS: 'üïê HORARIOS',
    DIRECCION: 'üìç DIRECCI√ìN',
    PORTAL: 'üíª PORTAL PACIENTES',
    RECETAS: 'üìß EMAIL RECETAS',
    MEDICOS: 'üë®‚Äç‚öïÔ∏è M√âDICOS',
    ESPECIALIDADES: 'ü©∫ ESPECIALIDADES'
  },

  ACCION: {
    CONTINUAR: ['CONTINUAR', 'SI, CONFIRMO', 'S√ç, CONFIRMO', 'CONFIRMAR TURNO'],
    VOLVER: ['VOLVER ATRAS', 'VOLVER ATR√ÅS', 'MENU PRINCIPAL', 'VOLVER AL MENU PRINCIPAL', '‚¨ÖÔ∏è VOLVER ATRAS']
  }
};


// ============================================
// 2. UTILIDADES - VALIDACI√ìN DE FORMATOS
// ============================================

/**
 * Valida si el texto es una selecci√≥n de lista (formato: TEXTO-COD o TEXTO-123)
 * @param {string} texto - Texto a validar
 * @returns {boolean}
 */
function esSeleccionLista(texto) {
  // Acepta formatos: CARDIOLOG√çA-CAR, NEUROLOG√çA-123, etc.
  return /^[A-Z√Ä-√ö√ë\s]+-[A-Z0-9]+$/i.test(texto);
}

/**
 * Valida si el texto es una selecci√≥n de horario (formato: MI√â 22/10 - 15:40)
 * @param {string} texto - Texto a validar
 * @returns {boolean}
 */
function esSeleccionHorario(texto) {
  return /^[A-Z√â]{3}\s\d{1,2}\/\d{1,2}\s-\s\d{2}:\d{2}$/.test(texto);
}

/**
 * Extrae el DNI de un texto, ignorando palabras y caracteres adicionales
 * @param {string} texto - Texto que puede contener un DNI
 * @returns {string|null} - DNI encontrado o null
 */
function extraerDNI(texto) {
  // Convertir a string
  const textoStr = texto.toString().trim();

  // Remover TODOS los caracteres que no sean d√≠gitos
  const soloDigitos = textoStr.replace(/\D/g, '');

  // Buscar secuencia de 7-8 d√≠gitos consecutivos
  const patron = /\d{7,8}/;
  const match = soloDigitos.match(patron);

  if (!match) return null;

  // Retornar los primeros 7-8 d√≠gitos encontrados
  const dni = match[0];

  // Si tiene m√°s de 8 d√≠gitos, tomar solo los primeros 8
  return dni.length > 8 ? dni.substring(0, 8) : dni;
}

/**
 * Valida si el texto contiene un DNI v√°lido (7-8 d√≠gitos)
 * @param {string} texto - Texto a validar
 * @returns {boolean}
 */
function esDNI(texto) {
  return extraerDNI(texto) !== null;
}

/**
 * Valida si el texto es un identificador de turno para cancelar
 * @param {string} texto - Texto a validar
 * @returns {boolean}
 */
function esTurnoParaCancelar(texto) {
  return /^[A-Z]+-\d{2}-\d{2}-\d{4}(-\d+)?/.test(texto) || 
         /^turno-\d{8}-\d{4}-\d+/.test(texto);
}

// ============================================
// 3. GESTI√ìN DE ESTADOS
// ============================================

/**
 * Normaliza el estado al formato jer√°rquico
 * @param {string} estado - Estado a normalizar
 * @returns {string} Estado normalizado
 */
function normalizarEstado(estado) {
  // Sin estado
  if (!estado || estado === '') return '';

  // Ya est√° normalizado (formato: SECCION:SUBSECCION)
  if (estado.includes(':')) return estado;

  // Estados con DNI (formato especial: DNI:12345678)
  if (estado.startsWith('DNI:')) {
    const dni = estado.replace('DNI:', '');
    return `DATOS:DNI_VALIDADO:${dni}`;
  }

  // Retornar el estado tal cual si no requiere normalizaci√≥n
  return estado;
}

/**
 * Extrae el contexto del estado jer√°rquico
 * @param {string} estado - Estado en formato jer√°rquico
 * @returns {object} Contexto con secci√≥n, subsecci√≥n y datos
 */
function obtenerContexto(estado) {
  if (!estado) {
    return { seccion: null, subseccion: null, datos: null };
  }

  const partes = estado.split(':');
  return {
    seccion: partes[0] || null,
    subseccion: partes[1] || null,
    datos: partes[2] || null
  };
}

// ============================================
// 4. REGLAS DE CLASIFICACI√ìN
// ============================================

/**
 * Reglas para el men√∫ principal
 */
function clasificarMenuPrincipal(mensaje, estado) {
  if (estado !== 'MENU_PRINCIPAL' && estado !== 'MENU_INICIAL') return null;

  if (mensaje === PATRONES.MENU.TURNOS) {
    return { categoria: 'NAV_TURNOS', nuevoEstado: 'TURNOS:TIPO_PACIENTE' };
  }

  if (mensaje === PATRONES.MENU.INFO) {
    return { categoria: 'NAV_INFO', nuevoEstado: 'INFO:MENU' };
  }

  if (mensaje === PATRONES.MENU.ADMIN) {
    return { categoria: 'NAV_ADMIN', nuevoEstado: 'ADMIN:CONFIRMAR' };
  }

  if (esDNI(mensaje)) {
    return { 
      categoria: 'INGRESO_DNI', 
      nuevoEstado: `DATOS:DNI_VALIDADO:${mensaje}` 
    };
  }

  return null;
}

/**
 * Reglas para gesti√≥n de turnos
 */
function clasificarGestionTurnos(mensaje, estado, ctx) {
  // Tipo de paciente
  if (estado === 'TURNOS:TIPO_PACIENTE') {
    if (mensaje === PATRONES.TURNOS.NUEVO) {
      return { categoria: 'NUEVO_PACIENTE', nuevoEstado: 'DATOS:RECOLECCION' };
    }
    if (mensaje === PATRONES.TURNOS.REGISTRADO) {
      return { categoria: 'PACIENTE_REGISTRADO', nuevoEstado: 'DATOS:SOLICITAR_DNI' };
    }
  }

  // Usuario con DNI validado
  if (ctx.seccion === 'DATOS' && ctx.subseccion === 'DNI_VALIDADO') {
    const dni = ctx.datos;

    // Usuario confirma sus datos despu√©s de validar DNI
    if (PATRONES.ACCION.CONTINUAR.includes(mensaje)) {
      return { 
        categoria: 'CONFIRMAR_DATOS_DNI', 
        nuevoEstado: 'TURNOS:TIPO_TURNO', 
        metadata: { dni } 
      };
    }

    if (mensaje === PATRONES.TURNOS.TOMAR) {
      return { 
        categoria: 'INICIAR_TOMAR_TURNO', 
        nuevoEstado: 'TURNOS:TIPO_TURNO', 
        metadata: { dni } 
      };
    }

    if (mensaje === PATRONES.TURNOS.CANCELAR) {
      return { 
        categoria: 'INICIAR_CANCELAR_TURNO', 
        nuevoEstado: 'CANCELAR:LISTAR', 
        metadata: { dni } 
      };
    }

    if (mensaje === PATRONES.TURNOS.MIS_TURNOS) {
      return { 
        categoria: 'LISTAR_MIS_TURNOS', 
        nuevoEstado: estado, 
        metadata: { dni } 
      };
    }
  }

  // Tipo de turno
  if (estado === 'TURNOS:TIPO_TURNO') {
    if (mensaje === PATRONES.TIPOS.CONSULTORIO) {
      return { categoria: 'TURNO_CONSULTORIO', nuevoEstado: 'TURNOS:ESPECIALIDADES' };
    }
    if (mensaje === PATRONES.TIPOS.ESTUDIO) {
      return { categoria: 'TURNO_ESTUDIO', nuevoEstado: 'TURNOS:ESPECIALIDADES_ESTUDIO' };
    }
  }

  // MEN√ö GESTI√ìN DE TURNOS (despu√©s de confirmar DNI)
  if (estado === 'MENU:GESTION_TURNOS') {
    if (mensaje === PATRONES.TURNOS.TOMAR) {
      return { 
        categoria: 'TOMAR_TURNO', 
        nuevoEstado: 'TURNOS:ESPECIALIDADES'
      };
    }
    if (mensaje === PATRONES.TURNOS.CANCELAR) {
      return { 
        categoria: 'CANCELAR_TURNO', 
        nuevoEstado: 'CANCELAR:LISTAR'
      };
    }
    if (mensaje === PATRONES.TURNOS.MIS_TURNOS) {
      return { 
        categoria: 'MIS_TURNOS', 
        nuevoEstado: 'TURNOS:MIS_TURNOS'
      };
    }
  }

  return null;
}

