/**
 * Reglas para recolecci√≥n de datos
 */
function clasificarDatos(mensaje, estado) {
  // Solicitar DNI - Usuario ingresa DNI para validaci√≥n (primera vez)
  if (estado === 'DATOS:SOLICITAR_DNI') {
    const dniExtraido = extraerDNI(mensaje);
    if (dniExtraido) {
      console.log('‚úÖ DNI extra√≠do:', dniExtraido, 'del mensaje:', mensaje);
      return { 
        categoria: 'VALIDAR_DNI', 
        nuevoEstado: `DATOS:DNI_VALIDADO:${dniExtraido}`,
        metadata: { dni: dniExtraido }
      };
    }
  }

  // DNI no encontrado (0) - Usuario reintenta con otro DNI
  if (estado === 'DATOS:DNI_VALIDADO:0') {
    const dniExtraido = extraerDNI(mensaje);
    if (dniExtraido) {
      console.log('üîÑ Reintentando validaci√≥n de DNI:', dniExtraido);
      return { 
        categoria: 'VALIDAR_DNI', 
        nuevoEstado: `DATOS:DNI_VALIDADO:${dniExtraido}`,
        metadata: { dni: dniExtraido }
      };
    }
  }

  // Recolecci√≥n inicial de datos
  if (estado === 'DATOS:RECOLECCION') {
  if (!PATRONES.ACCION.VOLVER.includes(mensaje) && mensaje !== 'CONTINUAR') {
      return { categoria: 'INGRESO_DATOS_PACIENTE', nuevoEstado: 'DATOS:RECOLECTADOS' };
    }
  }

  // Datos ya recolectados, esperando confirmaci√≥n
  if (estado === 'DATOS:RECOLECTADOS') {
  if (PATRONES.ACCION.CONTINUAR.includes(mensaje)) {
    return { categoria: 'CONFIRMAR_PACIENTE_NUEVO', nuevoEstado: 'DATOS:PACIENTE_NUEVO' };
    }
  }

  return null;
}

