/**
 * Reglas para navegaci√≥n hacia atr√°s
 */
function clasificarVolverAtras(mensaje, estado) {
  if (!PATRONES.ACCION.VOLVER.includes(mensaje)) return null;

  const navegacionAtras = {
    'TURNOS:HORARIOS': 'TURNOS:MEDICOS',
    'TURNOS:MEDICOS': 'TURNOS:ESPECIALIDADES',
    'TURNOS:MEDICOS:VER_MAS': 'TURNOS:MEDICOS',
    'TURNOS:ESPECIALIDADES': 'TURNOS:TIPO_TURNO',
    'TURNOS:ESPECIALIDADES:VER_MAS': 'TURNOS:ESPECIALIDADES',
    'TURNOS:CONFIRMAR': 'TURNOS:HORARIOS',
    'CANCELAR:LISTAR': 'MENU_PRINCIPAL',
    'DATOS:RECOLECTADOS': 'DATOS:RECOLECCION',
    'INFO:MENU': 'MENU_PRINCIPAL'
  };

  const nuevoEstado = navegacionAtras[estado] || 'MENU_PRINCIPAL';

  // Si volvemos al men√∫ principal, usar categor√≠a MENU_INICIAL
  const categoria = (nuevoEstado === 'MENU_PRINCIPAL') ? 'MENU_INICIAL' : 'VOLVER_ATRAS';

  return { categoria, nuevoEstado };
}

