// 5. CLASIFICADOR PRINCIPAL
// ============================================

/**
 * Clasificador principal - Ejecuta todas las reglas en orden
 * @param {string} mensaje - Mensaje del usuario
 * @param {string} estado - Estado actual
 * @returns {object} Resultado con categor√≠a y nuevo estado
 */
function clasificar(mensaje, estado) {
  const ctx = obtenerContexto(estado);

  // 1. INICIO / SIN ESTADO
  if (!estado || estado === '') {
    return { categoria: 'fallback', nuevoEstado: 'MENU_PRINCIPAL' };
  }

  // 2. VOLVER AL MEN√ö PRINCIPAL
  if (mensaje === 'MENU PRINCIPAL') {
    return { categoria: 'MENU_INICIAL', nuevoEstado: 'MENU_PRINCIPAL' };
  }

  // 3. BOT√ìN ADMINISTRACI√ìN (desde cualquier estado)
  if (mensaje === 'üè¢ ADMINISTRACION') {
    return { categoria: 'NAV_ADMIN', nuevoEstado: 'ADMIN:CONFIRMAR' };
  }

  // 4. EJECUTAR REGLAS EN ORDEN
  const reglas = [
    clasificarMenuPrincipal,
    clasificarGestionTurnos,
    clasificarSelecciones,
    clasificarDatos,
    clasificarCancelacion,
    clasificarInformacion,
    clasificarVolverAtras,
    clasificarAdmin
  ];

  for (const regla of reglas) {
    const resultado = regla(mensaje, estado, ctx);
    if (resultado) return resultado;
  }

  // 5. FALLBACK INTELIGENTE - Devuelve categor√≠a basada en el estado actual
  return obtenerCategoriaDeEstado(estado);
}

/**
 * Obtiene una categor√≠a por defecto basada en el estado actual
 * Esto permite que el flujo contin√∫e incluso cuando no se reconoce el mensaje
 * @param {string} estado - Estado actual del usuario
 * @returns {object} Resultado con categor√≠a y estado
 */
function obtenerCategoriaDeEstado(estado) {
  console.log('‚ö†Ô∏è FALLBACK INTELIGENTE - No se reconoci√≥ el mensaje');
  console.log('üìç Estado actual:', estado);

  // Mapa de estados a categor√≠as por defecto
  const estadoACategoriaMap = {
    // Estados de Datos
    'DATOS:SOLICITAR_DNI': { categoria: 'SOLICITAR_DNI', nuevoEstado: 'DATOS:SOLICITAR_DNI' },
    'DATOS:DNI_VALIDADO': { categoria: 'MENU_GESTION_TURNOS', nuevoEstado: 'MENU:GESTION_TURNOS' },

    // Estados del Men√∫ de Gesti√≥n
    'MENU:GESTION_TURNOS': { categoria: 'MENU_GESTION_TURNOS', nuevoEstado: 'MENU:GESTION_TURNOS' },
    'TURNOS:TIPO_TURNO': { categoria: 'MENU_TIPO_TURNO', nuevoEstado: 'TURNOS:TIPO_TURNO' },

    // Estados de Turnos - Especialidades
    'TURNOS:ESPECIALIDADES': { categoria: 'TURNO_CONSULTORIO', nuevoEstado: 'TURNOS:ESPECIALIDADES' },
    'TURNOS:ESPECIALIDADES:VER_MAS': { categoria: 'VER_MAS_ESPECIALIDADES', nuevoEstado: 'TURNOS:ESPECIALIDADES:VER_MAS' },
    'TURNOS:ESPECIALIDADES_ESTUDIO': { categoria: 'TURNO_ESTUDIO', nuevoEstado: 'TURNOS:ESPECIALIDADES_ESTUDIO' },

    // Estados de Turnos - M√©dicos
    'TURNOS:MEDICOS': { categoria: 'SELECCION_ESPECIALIDAD', nuevoEstado: 'TURNOS:MEDICOS' },
    'TURNOS:MEDICOS:VER_MAS': { categoria: 'VER_MAS_MEDICOS', nuevoEstado: 'TURNOS:MEDICOS:VER_MAS' },

    // Estados de Turnos - Horarios
    'TURNOS:HORARIOS': { categoria: 'SELECCION_MEDICO', nuevoEstado: 'TURNOS:HORARIOS' },
    'TURNOS:SIN_HORARIOS': { categoria: 'SELECCION_MEDICO', nuevoEstado: 'TURNOS:MEDICOS' },
    'TURNOS:CONFIRMAR': { categoria: 'SELECCION_HORARIO', nuevoEstado: 'TURNOS:CONFIRMAR' },

    // Estados de Cancelaci√≥n
    'CANCELAR:LISTAR': { categoria: 'CANCELAR_TURNO', nuevoEstado: 'CANCELAR:LISTAR' },
    'CANCELAR-TURNO': { categoria: 'EJECUTAR_CANCELACION_TURNO', nuevoEstado: 'CANCELAR-TURNO' },
    'CANCELAR:TURNO': { categoria: 'EJECUTAR_CANCELACION_TURNO', nuevoEstado: 'CANCELAR:TURNO' },

    // Estados de Mis Turnos
    'TURNOS:MIS_TURNOS': { categoria: 'MIS_TURNOS', nuevoEstado: 'TURNOS:MIS_TURNOS' },

    // Estados de Administraci√≥n
    'ADMIN:CONFIRMAR': { categoria: 'NAV_ADMIN', nuevoEstado: 'ADMIN:CONFIRMAR' },
    'ADMIN:HUMANO_ACTIVO': { categoria: 'DERIVAR_HUMANO', nuevoEstado: 'ADMIN:HUMANO_ACTIVO' },

    // Men√∫ Principal
    'MENU_PRINCIPAL': { categoria: 'MENU_INICIAL', nuevoEstado: 'MENU_PRINCIPAL' }
  };

  // Buscar categor√≠a por estado exacto
  if (estadoACategoriaMap[estado]) {
    const resultado = estadoACategoriaMap[estado];
    console.log('‚úÖ Categor√≠a encontrada para estado:', resultado.categoria);
    return resultado;
  }

  // Si no encuentra exacto, buscar por prefijo
  if (estado.startsWith('DATOS:DNI_VALIDADO:')) {
    console.log('‚úÖ Detectado DNI validado - Redirigiendo a men√∫ gesti√≥n');
    return { categoria: 'MENU_GESTION_TURNOS', nuevoEstado: 'MENU:GESTION_TURNOS' };
  }

  if (estado.startsWith('TURNOS:')) {
    console.log('‚úÖ Usuario en flujo de turnos - Manteniendo contexto');
    return { categoria: 'TURNO_CONSULTORIO', nuevoEstado: estado };
  }

  if (estado.startsWith('CANCELAR:')) {
    console.log('‚úÖ Usuario en cancelaci√≥n - Manteniendo contexto');
    return { categoria: 'CANCELAR_TURNO', nuevoEstado: estado };
  }

  if (estado.startsWith('DATOS:')) {
    console.log('‚úÖ Usuario en registro de datos');
    return { categoria: 'SOLICITAR_DNI', nuevoEstado: 'DATOS:SOLICITAR_DNI' };
  }

  // √öltimo recurso: categor√≠a fallback
  console.log('‚ö†Ô∏è Estado no reconocido o null - Categor√≠a fallback');
  return { categoria: 'fallback', nuevoEstado: 'MENU_PRINCIPAL' };
}

