/**
 * Construye el contexto enriquecido
 */
function construirContexto(resultado) {
  const nuevoCtx = obtenerContexto(resultado.nuevoEstado);

  return {
    seccion: nuevoCtx.seccion,
    subseccion: nuevoCtx.subseccion,
    es_usuario_registrado: resultado.nuevoEstado.startsWith('DATOS:DNI_VALIDADO'),
    en_proceso_turno: resultado.nuevoEstado.startsWith('TURNOS:'),
    en_cancelacion: resultado.nuevoEstado.startsWith('CANCELAR:')
  };
}

// ============================================
// 7. MAIN - PUNTO DE ENTRADA
// ============================================

// Obtener datos de entrada
const estadoRaw = $json.status || $json.value || $json.result || '';
const mensajeRaw = $('Variables').first().json.message;
const mensaje = mensajeRaw.toString().trim();
const contactNumber = $('Variables').first().json.contactNumber;

// Intentar obtener metadata del nodo anterior (para preservar c√≥digo de especialidad, etc.)
let metadataPrevio = {};
try {
  // Primero intentar del $json actual (puede venir del nodo Redis GET)
  if ($json.metadata && Object.keys($json.metadata).length > 0) {
    metadataPrevio = $json.metadata;
    console.log('‚úÖ Metadata encontrado en $json');
  } else {
    // Intentar del nodo Get Estado Actual
    metadataPrevio = $('Get Estado Actual').first().json.metadata || {};
    console.log('‚úÖ Metadata encontrado en Get Estado Actual');
  }
} catch (e) {
  try {
    // Intentar del nodo LISTA_MEDICOS
    const listaMedicos = $('LISTA_MEDICOS').first().json;
    metadataPrevio = {
      codigoEspecialidad: listaMedicos.codigoEspecialidad,
      especialidadSeleccionada: listaMedicos.especialidadSeleccionada
    };
    console.log('‚úÖ Metadata encontrado en LISTA_MEDICOS');
  } catch (e2) {
    try {
      // Intentar del nodo LISTA_ESPECIALIDADES
      metadataPrevio = $('LISTA_ESPECIALIDADES').first().json;
      console.log('‚úÖ Metadata encontrado en LISTA_ESPECIALIDADES');
    } catch (e3) {
      metadataPrevio = {};
      console.log('‚ö†Ô∏è No se encontr√≥ metadata previo');
    }
  }
}

console.log('üì¶ Metadata previo final:', JSON.stringify(metadataPrevio));

// Hacer metadata previo disponible globalmente para las funciones clasificadoras
globalThis._metadataPrevio = metadataPrevio;

// Normalizar estado
const estado = normalizarEstado(estadoRaw);

// ============================================
// üîç VERIFICACI√ìN DE CLIENTE REGISTRADO EN REDIS
// ============================================
// Intentar obtener datos del cliente desde el nodo PACIENTE
let pacienteRegistrado = false;
let datosCliente = null;

try {
  // Obtener del nodo PACIENTE (viene de Redis GET)
  const redisCliente = $('PACIENTE').first().json.paciente; // Nota: el campo se llama 'paciete' en el nodo

  if (redisCliente !== null && redisCliente !== undefined && redisCliente !== '') {
    pacienteRegistrado = true;
    datosCliente = redisCliente;
    console.log('‚úÖ Cliente REGISTRADO encontrado en Redis');
    console.log('üìã Datos del cliente:', JSON.stringify(datosCliente));
  } else {
    console.log('‚ùå Cliente NO registrado - Redis devolvi√≥ null');
  }
} catch (e) {
  console.log('‚ö†Ô∏è No se pudo obtener datos del nodo PACIENTE:', e.message);
  pacienteRegistrado = false;
}

console.log('üè• Paciente Registrado:', pacienteRegistrado);

// ============================================
// üö® DETECCI√ìN DE ERROR_MESSAGE (Errores de Registro)
// ============================================
// Si hay un error de registro (DNI incorrecto, nombre incompleto, etc.)
// debe devolver ERROR_REGISTRO para volver al flujo de datos
const hasError = $json.hasError || false;
const errorMessage = $json.errorMessage || null;

if (hasError && errorMessage) {
  console.log('‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ');
  console.log('üö® ERROR DE REGISTRO DETECTADO');
  console.log('üìù Mensaje de error:', errorMessage);
  console.log('üîÑ Redirigiendo al flujo de datos para reintentar');
  console.log('‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ');

  // Retornar inmediatamente con categor√≠a ERROR_REGISTRO
  return [{
    json: {
      categoria: 'ERROR_REGISTRO',
      mensaje: mensaje,
      contactNumber: contactNumber,
      pacienteRegistrado: pacienteRegistrado,
      datosCliente: datosCliente,
      estadoActual: 'DATOS:ERROR_REGISTRO',
