      metadata: {
        errorMessage: errorMessage,
        errorOriginal: errorMessage
      },
      redisKey: `state:${contactNumber}`,
      redisValue: 'DATOS:ERROR_REGISTRO',
      nuevoEstado: 'DATOS:ERROR_REGISTRO',
      _context: {
        seccion: 'DATOS',
        subseccion: 'ERROR_REGISTRO',
        es_usuario_registrado: false,
        en_proceso_turno: false,
        en_cancelacion: false,
        tiene_error: true
      }
    }
  }];
}

// Log de entrada
const dniEnMensaje = extraerDNI(mensaje);
console.log('‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ ENTRADA ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ');
console.log('üì© Mensaje raw:', JSON.stringify(mensajeRaw));
console.log('üì© Mensaje procesado:', mensaje);
console.log('üìè Longitud:', mensaje.length);
console.log('üìè Tipo:', typeof mensajeRaw);
console.log('üß™ Es DNI?:', esDNI(mensaje));
if (dniEnMensaje) {
  console.log('üÜî DNI encontrado:', dniEnMensaje);
}
console.log('üè∑Ô∏è  Estado raw:', estadoRaw);
console.log('üè∑Ô∏è  Estado normalizado:', estado);
console.log('‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ');

// Clasificar
const resultado = clasificar(mensaje, estado);

// Log de depuraci√≥n
logDebug(mensaje, estadoRaw, estado, resultado);

// Retornar resultado estructurado
return [{
  json: {
    // Datos principales
    categoria: resultado.categoria,
    mensaje: mensaje,
    contactNumber: contactNumber,

    // Verificaci√≥n de registro
    pacienteRegistrado: pacienteRegistrado,
