return $input.all().filter(item => {
  const estado = item.json.estado?.toLowerCase();
  const fechaStr = item.json.fecha_publicacion;
  const fecha = new Date(fechaStr);

  // Fecha actual del sistema (hoy)
  const hoy = new Date();
  hoy.setHours(0, 0, 0, 0); // Elimina la parte de horas para comparar solo fecha

  // Filtra si es "pendiente" y la fecha de publicacion es igual o anterior a hoy
  return estado === "pendiente" && fecha <= hoy;
});
